/**
 * QUESTION REPOSITORY ENGINE
 * Logic: 70% Theory (Knowledge/Code), 30% Calculation.
 * Total session capacity: 300 per section.
 */

const finalizeOptions = (correct: string, distractors: string[]) => {
  const optionsList = [
    { text: correct, isCorrect: true },
    { text: distractors[0], isCorrect: false },
    { text: distractors[1], isCorrect: false },
    { text: distractors[2], isCorrect: false }
  ];
  
  const seed = correct.length;
  for (let i = optionsList.length - 1; i > 0; i--) {
    const j = (seed * (i + 1)) % optionsList.length;
    [optionsList[i], optionsList[j]] = [optionsList[j], optionsList[i]];
  }
  
  const options: Record<string, string> = {};
  let correctKey = '';
  ['A', 'B', 'C', 'D'].forEach((key, i) => {
    options[key] = optionsList[i].text;
    if (optionsList[i].isCorrect) correctKey = key;
  });

  return { options, correctKey };
};

