export const generateQuestionFromEngine = (paper: Paper, index: number): Question => {
  const poolKey = Object.keys(Paper).find(key => Paper[key as keyof typeof Paper] === paper) || 'P2A1';
  const theoryPool = theoryPools[poolKey] || theoryPools['P2A1'];
  
  const isCalculation = index % 3 === 0 && (poolKey !== 'P2B2' && poolKey !== 'P2A1');
  const qId = `CHIEF-${poolKey}-${index.toString().padStart(3, '0')}`;

  if (isCalculation) {
    const inputs = Solver.getRandomInputs();
    const result = Solver.solveRankine(inputs.pHigh, inputs.tHigh, inputs.pLow);
    const correct = result.efficiency.toFixed(2) + '%';
    const { options, correctKey } = finalizeOptions(correct, [
      (result.efficiency * 0.8).toFixed(2) + '%',
      (result.efficiency * 1.2).toFixed(2) + '%',
      (result.efficiency + 10).toFixed(2) + '%'
    ]);

    return {
      id: qId,
      paper,
      topic: 'Thermodynamics',
      type: 'calculation',
      prompt: `Rankine System Diagnostic: Inlet P=${inputs.pHigh}MPa, T=${inputs.tHigh}°C, Exhaust P=${inputs.pLow}kPa. Determine thermal efficiency.`,
      options,
      correctKey,
      explanation: `Calculated via Rankine Logic: h1 (Superheat) and h2 (Ideal Exhaust) derived from boundary conditions. Eff = (W_turb - W_pump) / Q_in. Reference: ${correct}`,
      requiresCitation: false
    };
  } else {
    const entry = theoryPool[index % theoryPool.length];
    const { options, correctKey } = finalizeOptions(entry.correct, entry.distractors);
    
    return {
      id: qId,
      paper,
      topic: entry.topic,
      type: 'code_lookup',
      prompt: entry.prompt,
      options,
      correctKey,
      explanation: entry.explanation,
      requiresCitation: !!entry.citation,
      correctCitation: entry.citation || undefined
    };
  }
};

const generateCalcQuestion = (id: string, paper: Paper, index: number): Question => {
  if (paper.includes('2A1')) {
    const P = 1.5 + ((index % 50) * 0.05);
    const D = 1000 + (index % 100);
    const sol = Solver.solveAsmeHead(P, D, 115, 0.85);
    const { options, correctKey } = finalizeOptions(sol.thickness.toFixed(2) + ' mm', [(sol.thickness * 1.3).toFixed(2) + ' mm', (sol.thickness - 0.5).toFixed(2) + ' mm', '12.50 mm']);
    return {
      id, paper, topic: 'ASME I Shell Calc', type: 'calculation',
      prompt: `[CALC-MODE] Internal Pressure Diagnostic. Pressure: **${P.toFixed(2)} MPa**. ID: **${D} mm**. Allowable Stress: **115 MPa**. Efficiency: **0.85**. Calculate min shell thickness.`,
      options,
      correctKey, explanation: 'Thickness t = PR / (SE - 0.6P). R is Inside Radius.', requiresCitation: true, correctCitation: CodeBook.ASME_I
    };
  }
  
  if (paper.includes('2A2')) {
    const pHigh = 6 + (index % 6);
    const tHigh = 450 + (index % 50);
    const pLow = 10 + (index % 15);
    const sol = Solver.solveRankine(pHigh, tHigh, pLow);
    const { options, correctKey } = finalizeOptions(sol.efficiency.toFixed(2) + '%', [(sol.efficiency * 0.75).toFixed(2) + '%', (sol.efficiency + 8).toFixed(2) + '%', '14.20%']);
    return {
      id, paper, topic: 'Rankine Balance', type: 'calculation',
      prompt: `[CALC-MODE] Thermal Cycle Analysis. Inlet: **${pHigh} MPa** / **${tHigh}°C**. Condenser: **${pLow} kPa**. Determine overall thermal efficiency.`,
      options,
      correctKey, explanation: 'Net Work = Turbine Work - Pump Work. Eff = W_net / Q_in.', requiresCitation: false
    };
  }

  if (paper.includes('2A3')) {
    const rpm1 = 1750, rpm2 = 3500;
    const head1 = 40 + (index % 30);
    const sol = Solver.solveAffinity(rpm1, rpm2, 100, head1, 30);
    const { options, correctKey } = finalizeOptions(sol.head2.toFixed(1) + ' m', [(head1 * 2).toFixed(1) + ' m', (head1 * 4).toFixed(1) + ' m', '120.0 m']);
    return {
      id, paper, topic: 'Pump Scaling', type: 'calculation',
      prompt: `[CALC-MODE] Feed pump at **1750 RPM** develops **${head1} m** head. Calculate new head if speed is doubled to **3500 RPM**.`,
      options,
      correctKey, explanation: 'Affinity Law: H2 = H1 * (N2/N1)^2.', requiresCitation: false
    };
  }

  if (paper.includes('2B3')) {
    const I = 40 + (index % 60);
    const sol = Solver.solveElectrical(600, I, 0.85);
    const { options, correctKey } = finalizeOptions(sol.kw.toFixed(1) + ' kW', [(I * 0.5).toFixed(1) + ' kW', (sol.kw * 1.5).toFixed(1) + ' kW', '20.5 kW']);
    return {
      id, paper, topic: 'Circuit Loading', type: 'calculation',
      prompt: `[CALC-MODE] 3-Phase load check. Amperage: **${I} A**. Voltage: **600 V**. Power Factor: **0.85**. Calculate Real Power in kW.`,
      options,
      correctKey, explanation: 'P = sqrt(3) * V * I * PF / 1000.', requiresCitation: false
    };
  }

  // Final fallback
  const fallbackPool = theoryPools.P2A1;
  const entry = fallbackPool[0];
  const { options, correctKey } = finalizeOptions(entry.correct, entry.distractors);
  return { id, paper, topic: entry.topic, type: 'code_lookup', prompt: entry.prompt, options, correctKey, explanation: 'Fallback.', requiresCitation: false };
};
